import React from 'react';
import {
    PieChart, Pie, Cell, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import { FiUsers, FiMessageSquare, FiCheckCircle, FiClock } from 'react-icons/fi';
import { Card } from '../../../components/common/Card';
import { stats } from '../../../data/mock';
import styles from '../styles/Dashboard.module.css';

const StatCard = ({ title, value, icon, color }) => (
    <div className={styles.statCard}>
        <div className={styles.statIcon} style={{ backgroundColor: color }}>
            {icon}
        </div>
        <div className={styles.statInfo}>
            <h3>{value}</h3>
            <p>{title}</p>
        </div>
    </div>
);

export const Dashboard = () => {
    const COLORS = ['#0073aa', '#ffb900', '#46b450', '#d63638'];

    return (
        <div className={styles.dashboard}>
            {/* Stats Grid */}
            <div className={styles.statsGrid}>
                <StatCard title="Total Tickets" value={stats.total} icon={<FiMessageSquare />} color="#e5f5fa" />
                <StatCard title="Open Tickets" value={stats.open} icon={<FiUsers />} color="#fff8e5" />
                <StatCard title="Resolved" value="101" icon={<FiCheckCircle />} color="#edf8ee" />
                <StatCard title="Avg Resolution" value={stats.avgResolution} icon={<FiClock />} color="#f0f0f1" />
            </div>

            <div className={styles.chartsGrid}>
                {/* Status Chart */}
                <Card title="Ticket Status">
                    <div style={{ height: 300 }}>
                        <ResponsiveContainer width="100%" height="100%">
                            <PieChart>
                                <Pie
                                    data={stats.breakdown}
                                    cx="50%"
                                    cy="50%"
                                    innerRadius={60}
                                    outerRadius={80}
                                    paddingAngle={5}
                                    dataKey="value"
                                >
                                    {stats.breakdown.map((entry, index) => (
                                        <Cell key={`cell-${index}`} fill={entry.color} />
                                    ))}
                                </Pie>
                                <Tooltip />
                                <Legend />
                            </PieChart>
                        </ResponsiveContainer>
                    </div>
                </Card>

                {/* Priority Chart */}
                <Card title="Priority Breakdown">
                    <div style={{ height: 300 }}>
                        <ResponsiveContainer width="100%" height="100%">
                            <BarChart data={stats.priority}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="name" />
                                <YAxis />
                                <Tooltip />
                                <Bar dataKey="value" fill="#0073aa" />
                            </BarChart>
                        </ResponsiveContainer>
                    </div>
                </Card>
            </div>

            {/* Recent Activity or other widgets could go here */}
        </div>
    );
};
