import React from 'react';
import { FiLogOut, FiPlus, FiUser } from 'react-icons/fi';
import { Badge } from '../../../components/common/Badge';
import { Button } from '../../../components/common/Button';
import styles from '../styles/TicketSidebar.module.css';

export const TicketSidebar = ({ tickets, selectedId, onSelect, onLogout }) => {
    return (
        <div className={styles.sidebar}>
            <div className={styles.header}>
                <div className={styles.profile}>
                    <div className={styles.avatar}>
                        <FiUser />
                    </div>
                    <div className={styles.userInfo}>
                        <span className={styles.userName}>Jane Doe</span>
                        <span className={styles.userEmail}>jane@client.com</span>
                    </div>
                </div>
                <button className={styles.logoutBtn} onClick={onLogout} title="Logout">
                    <FiLogOut />
                </button>
            </div>

            <div className={styles.actions}>
                <Button variant="primary" size="sm" style={{ width: '100%' }}>
                    <FiPlus style={{ marginRight: 5 }} /> New Ticket
                </Button>
            </div>

            <div className={styles.ticketList}>
                {tickets.map(ticket => (
                    <div
                        key={ticket.id}
                        className={`${styles.ticketItem} ${selectedId === ticket.id ? styles.active : ''}`}
                        onClick={() => onSelect(ticket.id)}
                    >
                        <div className={styles.ticketHeader}>
                            <span className={styles.ticketId}>{ticket.id}</span>
                            <span className={styles.ticketDate}>{ticket.created}</span>
                        </div>
                        <h4 className={styles.ticketSubject}>{ticket.subject}</h4>
                        <div className={styles.ticketFooter}>
                            <Badge status={ticket.status} />
                            <span className={styles.category}>{ticket.category}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};
